mapper.select:
 select
  id, bookName, author,bookType, bookPrice, pubDate, bookPub, bookPic, weiXin, phone, des, createTime, createUser
 from bookInfo
 where dataStatus = 1
and bookName like '%' #{bookName} '%'
 and bookType = #{bookType} 
 and bookPrice >= #{startPrice} 
 and bookPrice <= #{endPrice} 
 and createTime >= #{startTime} 
 and createTime < #{endTime} 
 and createUser = #{createUser} 
 order by createTime desc

select
 id, bookName, author,bookType, bookPrice, pubDate, bookPub, bookPic, weiXin, phone, des, createUser, createTime
from bookInfo where id = #{id}

mapper.update:
update bookInfo
bookName=#{bookName}, author=#{author}, bookPrice=#{bookPrice}, pubDate=#{pubDate}, bookPub=#{bookPub}, bookPic=#{bookPic}, weiXin=#{weiXin}, phone=#{phone}, des=#{des}, updateUser=#{updateUser.id}, updateTime=#{updateTime}
where id = #{id}

mapper.delete:
update bookInfo
set dataStatus = 100
where

    id = #{id}

        id in

            #{id}

